import React, { useState, useReducer} from 'react'

export const CartContext = React.createContext({
	addedMeals: [],
	totalAmount: 0,
	onAddMeal: () => {},
})

const ADD_MEAL_TYPE = "ADD_MEAL"


const reducer = (state, action) =>{
	switch (action.type) {
		case ADD_MEAL_TYPE: {
			const prevMeals = state.addedMeals

			if(prevMeals.length === 0){
				return {
					...state,
					addedMeals: [action.payload],
				}
			}
		}
			
			break;
	
		default:
			break;
	}
	return state
}
export const CartProvider = ({ children }) => {
	const [addedMeals, setAddedMeals] = useState([])

	const [cartState, dispatch] = useReducer(reducer, {addedMeals: []})

	const addMealHandler = (newMeal) => {
		const currentIndex = addedMeals.findIndex((m) => m.id === newMeal.id)
		if (currentIndex === -1) {
			return setAddedMeals([...addedMeals, newMeal])
		}
		const newMeals = addedMeals.map((meal) => {
			if (meal.id === newMeal.id) {
				return {
					...meal,
					amount: meal.amount + newMeal.amount,
				}
			}
			return meal
		})
		setAddedMeals(newMeals)
	}

	return (
		<CartContext.Provider
			value={{
				addedMeals,
				onAddMeal: addMealHandler,
				totalAmount: 0,
			}}
		>
			{children}
		</CartContext.Provider>
	)
}
