import React from "react";
import { IconButton } from "../UI/IconButton";
import { MinusIcon, PlusIcon } from "../assets";
import { styled } from "styled-components";

export const CartItem = ({
  title = "about item",
  price = "22.99",
  amount = "5",
}) => {
  return (
    <ListItem>
      <Content>
        <p>{title}</p>

        <PriceContainer>
          <span>{price}</span>
          <span>x{amount}</span>
        </PriceContainer>
      </Content>
      <div>
        <IconButton Icon={MinusIcon} />
        <IconButton Icon={PlusIcon} />
      </div>
    </ListItem>
  );
};

const PriceContainer = styled("div")`
  display: flex;
  align-items: center;
  gap: 3rem;
  & > .price {
    color: #ad5502;
    font-size: 18px;
    font-weight: 600;
  }
  & > .amount {
    border: 1px solid #d6d6d6;
    border-radius: 6px;
    padding: 6px 14px;
    color: #222222;
    font-weight: 500;
  }
`;

const Content = styled.div`
  &h2 {
    color: #222222;
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 12px;
  }
`;
const ListItem = styled.li`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 24px 0;
  border-bottom: 1px solid #d6d6d6;
  &:first-of-type {
    padding-top: 0;
  }
`;
