import React, { useState,useReducer } from 'react'

export const CartContext = React.createContext({
	// addedMeals: [],
	// totalAmount: 0,
	// onAddMeal: () => {},
})
const initialState = {
	addedMeals: [],
	totalAmount: 0,
}
const reducerCartFn = (state, action) =>{
	switch (action.type) {
	   case 'SET_ADD_MEALS':
		return{ ...state, addedMeals: action.payload}
	   case 'SET_TOTAL_AMOUNT': 
		return{ ...state, totalAmount: action.payload}
	   default:
		return state
	}
	  }

export const CartProvider = ({ children }) => {

	const [state, dispatch] = useReducer(reducerCartFn, initialState);



	// const [addedMeals, setAddedMeals] = useState([])

	const addMealHandler = (newMeal) => {
		let data = []
		const currentIndex = data.findIndex((m) => m.id === newMeal.id)
		if (currentIndex === -1) {
			return data([...data, newMeal])
		}

		const newMeals = state.map((meal) => {
			if (meal.id === newMeal.id) {
				return {
					...meal,
					amount: meal.amount + newMeal.amount,
				}
			}
			return meal
		})
		data(newMeals)
		dispatch({ type: 'SET_ADD_MEALS',payload: data })
		
	}

	return (
		<CartContext.Provider
			value={{
				addedMeals: [],
				onAddMeal: addMealHandler,
				totalAmount: 0,
			}}
		>
			{children}
		</CartContext.Provider>
	)
}
